'use strict';

/**
 *  Module: storage-utils.js
 *
 * Collection of functions for storage public method.
 */

/**
 * Get object hash name.
 */
function getObjectHashName(hashPrefix, id) {
    return hashPrefix + ':' + id;
}
exports.getObjectHashName = getObjectHashName;

/**
 * Get object set name.
 */
function getObjectSet(objectName) {
    return 'S:' + objectName;
}
exports.getObjectSet = getObjectSet;

/**
 * Get temporary set name.
 */
function getNewObjectSet(objectName) {
    return 'S:' + objectName + '.new';
}
exports.getNewObjectSet = getNewObjectSet;

/***
 * Get sort format string with field.
 */
function getSortField(resourceType, field) {
    return resourceType + ':*->' + field;
}
exports.getSortField = getSortField;
