var base = require("../../client/base"),
    MeterManager = require("./meter"),
    AlarmManager = require('./alarm'),
    ResourceAlarmHistoryManager = require('./resource_alarm_history'),
    ResourceStateManager = require('./resource_state'),
    EventManager = require("./event"),
    WorkflowEventManager = require("./workflow_event"),
    ResourceManager = require("./resource");

var Ceilometer = base.Client.extend({
    service_type: "metering",
    version: "2",
    init: function (options) {
      this._super(options);
      this.meter = new MeterManager(this);
      this.alarm = new AlarmManager(this);
      this.resource = new ResourceManager(this);
      this.event = new EventManager(this);
      this.workflow_event = new WorkflowEventManager(this);
      this.resource_alarm_history = new ResourceAlarmHistoryManager(this);
      this.resource_state = new ResourceStateManager(this);
    }
});

module.exports = Ceilometer;
