var base = require("../../client/base"),
    error = require("../../client/error"),
    urljoin = require("../../client/utils").urljoin;


var DeployNodeManager = base.Manager.extend({
    namespace: "/deploy/node",
    plural: "nodes",

    get_base_url: function (params) {
      var base_url = this._super(params);
      if (params.detail) {
        base_url = this.urljoin(base_url, 'detail');
      }
      return base_url;
    },

    update: function (params, callback) {
        params.url = urljoin(this.get_base_url(params), params.id);
        this.client.put(params, callback);
    }
});


module.exports = DeployNodeManager;
