var base = require("../../client/base");
    HostManager = require('./host');
    PackageManager = require('./package');
    PlanManager = require('./plan');
    HypervisorManager = require('./hypervisor');
    NetworkManager = require('./network');

var Egis = base.Client.extend({
  service_type: "recovery",
  version: "1",

  init: function (options) {
    this._super(options);

    this.hosts = new HostManager(this);
    this.packages = new PackageManager(this);
    this.plans = new PlanManager(this);
    this.hypervisors = new HypervisorManager(this);
    this.networks = new NetworkManager(this);
    }
});

module.exports = Egis;
