var base = require("../../client/base");
    utils = require("../../client/utils");

var NetworkManager = base.Manager.extend({
  namespace: "/hypervisors/{hypervisors_id}/networks",
  plural: "networks",

  prepare_namespace: function (params) {
    console.log(params,"params");
    params.data = params.data || {};
    var hypervisors_id = params.params.hypervisors_id
    delete params.params.hypervisors_id;

    return utils.interpolate(this.namespace, {hypervisors_id: hypervisors_id});
  },

})

module.exports = NetworkManager;
