var base = require("../../client/base"),
    error = require("../../client/error"),
    interpolate = require("../../client/utils").interpolate;

/**
 * Manager-alike helper for get user list of a project.
 *
 * @type {UsersInOneProjectManager}
 */
var UsersInOneProjectManager = base.Manager.extend({
  namespace: "/tenants/{tenant_id}/users",
  plural: "users",

  prepare_namespace: function (params) {
    return interpolate(this.namespace, {tenant_id: params.project});
  }

});
module.exports = UsersInOneProjectManager;
