var base = require("../../client/base"),
    error = require("../../client/error"),
    interpolate = require("../../client/utils").interpolate;

/**
 * Manager-alike helper for get projects one user belongs to.
 *
 * @type {ProjectsUserBelongsToManager}
 */
var ProjectsUserBelongsToManager = base.Manager.extend({
  namespace: "/users/{user_id}/projects",
  plural: "projects",

  prepare_namespace: function (params) {
    return interpolate(this.namespace, { user_id: params.user});
  }

});
module.exports = ProjectsUserBelongsToManager;
