var base        = require("../../client/base"),
    interpolate = require("../../client/utils").interpolate;

var UserGroupsManager = base.Manager.extend({
  namespace: "/users/{user_id}/groups",
  plural: "groups",


  prepare_namespace: function(params) {
    return interpolate(this.namespace, { user_id: params.data.user });   }
});

module.exports = UserGroupsManager;
