var async = require('async'),
    base = require("../../client/base"),
    error = require("../../client/error");


var AppManager = base.Manager.extend({
    namespace: "apps",
    plural: "apps",

    all: function (params, callback) {
        params.manager_method = 'all';
        params.url = this.get_base_url(params);
        params.result_key = 'app-list';
        this.client.get(params, callback);
    },

    getImage: function (params, callback) {
        params.url = this.get_base_url(params) + params.res;
        this.client.get(params, callback);
    },

    update: function (params, callback) {
        params.data = {
            "apps": params.data
        }
        params.url = this.get_base_url(params) + "/";
        this.client.put(params, callback);
    }
});


module.exports = AppManager;
