var base = require("../../client/base");

var DeployNodeManager = require("./deploy_node"),
    DeployTemplateManager = require("./deploy_template"),
    DeployEventManager = require("./deploy_event"),
    DeployRoleManager = require("./deploy_role");


var Deployment = base.Client.extend({
  service_type: "deployment",
  version: "1.0",

  init: function (options) {
    this._super(options);

    this.deployNodes = new DeployNodeManager(this);
    this.deployTemplates = new DeployTemplateManager(this);
    this.deployRoles = new DeployRoleManager(this);
    this.deployEvents = new DeployEventManager(this);
  }
});

module.exports = Deployment;
