var async = require('async'),
    base = require("../../client/base"),
    error = require("../../client/error"),
    interpolate = require("../../client/utils").interpolate;


var DeployTemplateManager = base.Manager.extend({
    namespace: "/{project_id}/deploy/template",
    plural: "templates",

    prepare_namespace: function (params) {
        return interpolate(this.namespace, {project_id: params.project});
    },

    update: function (params, callback) {
        params.manager_method = "update";
        params = this.prepare_params(params, this.get_base_url(params), "singular");
        params.data = params.data[this.singular]
        params.url = this.get_base_url(params);
        return this.client[params.http_method || this.method_map.update](params, callback);
    }
});


module.exports = DeployTemplateManager;
