var base = require("../../client/base")
    AccountManager   = require('./account');
    CloudsManager    = require('./clouds');
    RegionsManager   = require('./regions');
    InstancesManager = require('./instance');
    FlavorsManager = require('./flavors');
    ImagesManager = require('./images');
    SecuritygroupsManager = require('./securitygroups');



var Duckbill = base.Client.extend({
  service_type: "pubcloud",
  version: "1",

  init: function (options) {
    this._super(options);

    this.accounts       = new AccountManager(this);
    this.clouds         = new CloudsManager(this);
    this.regions        = new RegionsManager(this);
    this.instances      = new InstancesManager(this);
    this.flavors        = new FlavorsManager(this);
    this.images         = new ImagesManager(this);
    this.securitygroups = new SecuritygroupsManager(this);

    }
});

module.exports = Duckbill;
