var base = require("../../client/base"),
    error = require("../../client/error"),
    urljoin = require("../../client/utils").urljoin;

var RouterManager = base.Manager.extend({
  namespace: "routers",    
  plural: "routers",
  error_key: "NeutronError",

  addInterface: function (params, callback) {
    var url = urljoin(this.get_base_url(params), params.id || params.data.id, "add_router_interface");
    params.url = url;
    return this.client.put(params, callback);
  },

  removeInterface: function (params, callback) {
    var url = urljoin(this.get_base_url(params), params.id || params.data.id, "remove_router_interface");
    params.url = url;
    return this.client.put(params, callback);
  }
});

module.exports = RouterManager;
