var base = require("../../client/base"),
    error = require("../../client/error");


var TemplateManager = base.Manager.extend({
    namespace: "load_template",
    plural: "load_template",
    singular: "load_template",

    load_template: function (params, callback) {
        params.url = this.get_base_url(params) + "/" + params.id;
        this.client.get(params, callback);
    }
});

module.exports = TemplateManager;
