var base = require("../../client/base"),
    error = require("../../client/error");


var RegisterManager = base.Manager.extend({
    namespace: "register",
    singular: "register",

    validate: function (params, callback) {
        params.url = this.get_base_url(params);
        if (params.name) {
          params.url = params.url + "?name=" + params.name
        } else if (params.email) {
          params.url = params.url + "?email=" + params.email
        }
        this.client.get(params, callback);
    },

    register: function (params, callback) {
        params.url = this.get_base_url(params);
        this.client.post(params, callback);
    }
});


module.exports = RegisterManager;
