var async = require('async'),
    base = require("../../client/base"),
    error = require("../../client/error");


var WorkflowRequestTypeManager = base.Manager.extend({
    namespace: "workflow-request-types",
    plural: "workflow-request-types",
    singular: "workflow-request-types",

    create: function (params, callback) {
        params.manager_method = "create";
        params = this.prepare_params(params, this.get_base_url(params), "singular");
        params.result_key = 'workflow-request-type';
        return this.client[params.http_method || this.method_map.create](params, callback);
    },

    update: function (params, callback) {
        params.data = {
            "workflow-request-type": params.data
        }
        params.url = this.get_base_url(params) + "/" + params.id;
        this.client.put(params, callback);
    }
});


module.exports = WorkflowRequestTypeManager;
